var Detecter={browser:{name:'not Detected Browser',version:'not Detected version'},device:{name:'not Detected Device',properties:{width:'not Detected width',height:'not Detected height'}}};Detecter.detectBrowser=function(){var version;if(/Firefox[\/\s](\d+\.\d+)/.test(navigator.userAgent)){this.browser.name="firefox";version=new Number(RegExp.$1);this.browser.version=Math.floor(version);}if(/MSIE (\d+\.\d+);/.test(navigator.userAgent)){this.browser.name="IE";version=new Number(RegExp.$1);this.browser.version=Math.floor(version);}if(/Opera[\/\s](\d+\.\d+)/.test(navigator.userAgent)){this.browser.name="opera";version=new Number(RegExp.$1);this.browser.version=Math.floor(version);}if((navigator.userAgent.toLowerCase().indexOf('chrome')>-1)&&(navigator.userAgent.toLowerCase().indexOf('safari')!=-1)){this.browser.name="chrome";version=new Number(RegExp.$1);this.browser.version=Math.floor(version);}if((navigator.userAgent.toLowerCase().indexOf('chrome')==-1)&&(navigator.userAgent.toLowerCase().indexOf('safari')!=-1)){this.browser.name="safari";version=new Number(RegExp.$1);this.browser.version=Math.floor(version);}};Detecter.detectDevice=function(){if(navigator.userAgent.match(/iPhone/i)){this.device.name="iphone";}else if(navigator.userAgent.match(/iPod/i)){this.device.name="ipod";}else if(navigator.userAgent.match(/iPad/i)){this.device.name="ipad";}else if(navigator.userAgent.match(/Android/i)){this.device.name="android";}else if(navigator.userAgent.match(/BlackBerry/i)){this.device.name="blackberry";}else if(navigator.userAgent.match(/IEMobile/i)){this.device.name="iemobile";}else if(navigator.userAgent.match(/Silk/i)){this.device.name="kindle";}else{this.device.name="computer";}};Detecter.printDetecter=function(){console.log('Browser: '+this.browser.name+'\nVersion: '+this.browser.version+'\nDevice: '+this.device.name);};